SELECT schools.id, schools.name AS school, courses.name AS course, companies.name AS company, roles.name AS role FROM users JOIN educations ON users.id = educations."userId" JOIN schools ON schools.id = educations."schoolId" JOIN courses ON courses.id = educations."courseId" JOIN applicants ON users.id = applicants."userId" JOIN jobs ON jobs.id = applicants."jobId" JOIN companies ON companies.id = jobs."companyId" JOIN roles ON roles.id = jobs."roleId" WHERE companies.id = 10 AND roles.name = 'Software Engineer' AND jobs.active = true ORDER BY schools.id;